{

  "definitions": {},

  "$schema": "http://json-schema.org/draft-07/schema#",

  "$id": "http://example.com/root.json",

  "type": "object",

  "title": "The Root Schema",

  "required": [

    "data",

    "meta"

  ],

  "properties": {

    "data": {

      "$id": "#/properties/data",

      "type": "array",

      "title": "The Data Schema",

      "items": {

        "$id": "#/properties/data/items",

        "type": "object",

        "title": "The Items Schema",

        "required": [

          "departmentId",

          "name"

        ],

        "properties": {

          "departmentId": {

            "$id": "#/properties/data/items/properties/departmentId",

            "type": "string",

            "title": "The Departmentid Schema",

            "default": "",

            "examples": [

              "01"

            ],

            "pattern": "^(.*)$"

          },

          "name": {

            "$id": "#/properties/data/items/properties/name",

            "type": "string",

            "title": "The Name Schema",

            "default": "",

            "examples": [

              "Deli"

            ],

            "pattern": "^(.*)$"

          }

        }

      }

    },

    "meta": {

      "$id": "#/properties/meta",

      "type": "object",

      "title": "The Meta Schema",

      "required": [

        "pagination"

      ],

      "properties": {

        "pagination": {

          "$id": "#/properties/meta/properties/pagination",

          "type": "object",

          "title": "The Pagination Schema",

          "required": [

            "start",

            "limit",

            "total"

          ],

          "properties": {

            "start": {

              "$id": "#/properties/meta/properties/pagination/properties/start",

              "type": "integer",

              "title": "The Start Schema",

              "default": 0,

              "examples": [

                1

              ]

            },

            "limit": {

              "$id": "#/properties/meta/properties/pagination/properties/limit",

              "type": "integer",

              "title": "The Limit Schema",

              "default": 0,

              "examples": [

                111

              ]

            },

            "total": {

              "$id": "#/properties/meta/properties/pagination/properties/total",

              "type": "integer",

              "title": "The Total Schema",

              "default": 0,

              "examples": [

                111

              ]

            }

          }

        }

      }

    }

  }

}
